import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import './styles.css';

function Game() {
    const { storyId } = useParams();
    const [messages, setMessages] = useState([
        { role: 'system', text: 'You are now playing the puzzle: ' + storyId.replace(/-/g, ' ') + '.' },
        { role: 'system', text: 'Ask yes/no questions to solve the mystery!' }
    ]);

    const [userInput, setUserInput] = useState('');

    const handleSend = () => {
        if (userInput.trim() === '') return;
        // Just simulate a "response"
        const newMessages = [
            ...messages,
            { role: 'user', text: userInput },
            { role: 'assistant', text: 'I am just a placeholder response...' }
        ];
        setMessages(newMessages);
        setUserInput('');
    };

    return (
        <div className="game-container">
            <header className="menu">
                <nav>
                    <Link to="/" className="menu-btn">Back to stories</Link>
                </nav>
                <h1 className="title">Playing: {storyId.replace(/-/g, ' ')}</h1>
            </header>

            <div className="chat-window" style={{ width: '80%', maxWidth: '600px', margin: '20px auto', background: '#fff', padding: '20px', borderRadius: '10px' }}>
                <div className="messages" style={{ marginBottom: '20px', height: '300px', overflowY: 'auto', border: '1px solid #ddd', padding: '10px', borderRadius: '5px' }}>
                    {messages.map((msg, index) => (
                        <div key={index} style={{ margin: '10px 0' }}>
                            <strong>{msg.role === 'user' ? 'You:' : 'Game:'}</strong> {msg.text}
                        </div>
                    ))}
                </div>
                <div className="input-area" style={{ display: 'flex', gap: '10px' }}>
                    <input
                        type="text"
                        style={{ flex: '1', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
                        value={userInput}
                        onChange={(e) => setUserInput(e.target.value)}
                        placeholder="Ask a question..."
                    />
                    <button className="menu-btn" onClick={handleSend}>Send</button>
                </div>
            </div>
        </div>
    );
}

export default Game;
