import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';

function Game() {
    const { puzzleId } = useParams();
    const [messages, setMessages] = useState([
        { role: 'system', content: `You have selected the "${puzzleId.replace(/-/g, ' ')}" puzzle. Ask yes/no questions to discover the key points!` }
    ]);
    const [inputValue, setInputValue] = useState('');

    const handleSend = () => {
        if (!inputValue.trim()) return;

        let response = "I can’t answer that right now.";
        if (inputValue.toLowerCase().includes('hiccup')) {
            response = "Yes, the man has hiccups.";
        } else if (inputValue.toLowerCase().includes('scare')) {
            response = "Yes, scaring him helped.";
        }

        setMessages(prev => [...prev, { role: 'user', content: inputValue }, { role: 'assistant', content: response }]);
        setInputValue('');
    };

    return (
        <div style={{padding: '20px'}}>
            <Link to="/" style={{textDecoration: 'none', color: '#63918b', fontWeight: 'bold'}}>← Back</Link>
            <h2>{puzzleId.replace(/-/g, ' ')}</h2>
            <div style={{
                border: '1px solid #ddd',
                borderRadius: '10px',
                height: '400px',
                overflowY: 'auto',
                padding: '10px',
                marginBottom: '20px',
                backgroundColor: '#fff'
            }}>
                {messages.map((m, i) => (
                    <div key={i} style={{ marginBottom: '10px' }}>
                        <strong>{m.role === 'assistant' ? 'LLM' : m.role === 'user' ? 'You' : 'System'}:</strong> {m.content}
                    </div>
                ))}
            </div>
            <div style={{display:'flex', gap:'10px'}}>
                <input
                    type="text"
                    style={{flex:1, padding:'10px', borderRadius:'5px', border:'1px solid #ccc'}}
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    placeholder="Ask a question..."
                />
                <button onClick={handleSend} style={{padding:'10px 20px', border:'none', borderRadius:'5px', backgroundColor:'#63918b', color:'#fff', cursor:'pointer'}}>Send</button>
            </div>
        </div>
    );
}

export default Game;
